mapper.select:
SELECT rscr.customer_id AS id, rscr.log_out_status AS logOutStatus, min(rcd.customer_name) AS name, rscr.customer_account AS account, 0 AS companyId, IFNULL(sum( resRt.num ),0) AS orderCount, IFNULL(sum( resRt.price ),0) AS amount, IFNULL(sum( resRti.num ),0) AS skuCount, IFNULL(sum( resPay.payPrice),0) AS payAmount, IFNULL(sum( resPay.payNUm ),0) AS payOrderCount, case when IFNULL(sum( resRt.num ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( resRt.price ),0) / IFNULL(sum( resRt.num ),0), DECIMAL ( 15, 2 ) ),0) end orderPerPrice, IFNULL(sum( resRro.num ),0) AS returnCount, IFNULL(sum( resRro.rPrice ),0) AS returnAmount, IFNULL(sum( resRri.num ),0) AS returnSkuCount, IFNULL(count(rscr.customer_id),0) as customerCount FROM replay_customer rscr LEFT JOIN replay_customer_detail rcd ON rcd.customer_id = rscr.customer_id
GROUP BY rscr.customer_id order by sum( resRt.price ) desc
limit #{number,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}



group by rcl.customer_level_id
order by sum( resRt.price ) desc
limit #{number,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

select * from (


GROUP BY
rsl.store_level_id,
rscr.company_info_id
order by sum( resRt.price ) desc
) result where result.companyId = #{companyInfoId}
limit #{number,jdbcType=INTEGER}, #{size,jdbcType=INTEGER}

