mapper.select:
SELECT c.STAT_DATE, c.CUSTOMER_ID,c.CUSTOMER_NAME, c.CUSTOMER_ACCOUNT, c.ORDER_NUM, c.ORDER_MONEY, c.ORDER_GOODS_NUM, c.PAY_NUM, c.PAY_MONEY, c.ORDER_PER_PRICE, c.REFUND_NUM, c.REFUND_MONEY, c.REFUND_GOODS_NUM, c.SHOP_ID, c.CREATE_TM
FROM customer_day c
FROM customer_recent_seven c
FROM customer_recent_thirty c
FROM customer_month c
LEFT JOIN replay_store r on c.SHOP_ID = r.company_info_id where 1=1
and c.SHOP_ID = #{customerOrderQueryRequest.companyId}
and c.CUSTOMER_ID = #{customerOrderQueryRequest.customerId}
and (c.customer_name like CONCAT('%',#{customerOrderQueryRequest.queryText},'%') or c.customer_account like CONCAT('%',#{customerOrderQueryRequest.queryText},'%'))
and c.STAT_DATE = #{daylyDate}
order by ${customerOrderQueryRequest.sortField} ${customerOrderQueryRequest.sortTypeText}
limit #{pageRequest.start} , #{pageRequest.pageSize}

SELECT customer_report_level.id as ID, customer_report_level.STAT_DATE as STAT_DATE, customer_report_level.CUSTOMER_LEVEL_ID as CUSTOMER_LEVEL_ID, customer_report_level.CUSTOMER_LEVEL_NAME as CUSTOMER_LEVEL_NAME, customer_report_level.ORDER_NUM as ORDER_NUM, customer_report_level.ORDER_MONEY as ORDER_MONEY, customer_report_level.ORDER_GOODS_NUM as ORDER_GOODS_NUM, customer_report_level.PAY_NUM as PAY_NUM, customer_report_level.PAY_MONEY as PAY_MONEY, customer_report_level.USER_PER_PRICE_THIRTY as USER_PER_PRICE_THIRTY, customer_report_level.ORDER_PER_PRICE as ORDER_PER_PRICE, customer_report_level.REFUND_NUM as REFUND_NUM, customer_report_level.REFUND_MONEY as REFUND_MONEY, customer_report_level.REFUND_GOODS_NUM as REFUND_GOODS_NUM, customer_report_level.SHOP_ID as SHOP_ID, customer_report_level.CREATE_TM as CREATE_TM
FROM customer_level_day customer_report_level
FROM customer_level_recent_seven customer_report_level
FROM customer_level_recent_thirty customer_report_level
FROM customer_level_month customer_report_level
left join replay_customer_level level on customer_report_level.CUSTOMER_LEVEL_ID = level.customer_level_id
left join replay_store_level rcl on customer_report_level.CUSTOMER_LEVEL_ID = rcl.store_level_id
where 1=1
and SHOP_ID = #{customerOrderQueryRequest.companyId}
and level.customer_level_name = #{customerOrderQueryRequest.queryText}
and rcl.level_name = #{customerOrderQueryRequest.queryText}
and customer_report_level.STAT_DATE = #{daylyDate}
order by customer_report_level.${customerOrderQueryRequest.sortField} ${customerOrderQueryRequest.sortTypeText}
limit #{pageRequest.start} , #{pageRequest.pageSize}

select c.id, c.STAT_DATE, c.CITY_ID, c.ORDER_NUM, c.ORDER_MONEY, c.ORDER_GOODS_NUM, c.PAY_NUM, c.PAY_MONEY, c.USER_PER_PRICE_THIRTY, c.ORDER_PER_PRICE, c.REFUND_NUM, c.REFUND_MONEY, c.REFUND_GOODS_NUM, c.SHOP_ID, c.CREATE_TM
FROM customer_region_day c
FROM customer_region_recent_seven c
FROM customer_region_recent_thirty c
FROM customer_region_month c
where 1 = 1
and c.SHOP_ID = #{customerOrderQueryRequest.companyId}
and c.city_id in
#{item}
and c.STAT_DATE = #{daylyDate}#
order by ${customerOrderQueryRequest.sortField} ${customerOrderQueryRequest.sortTypeText}
limit #{pageRequest.start} , #{pageRequest.pageSize}

select count(1)
FROM customer_day c
FROM customer_recent_seven c
FROM customer_recent_thirty c
FROM customer_month c
LEFT JOIN replay_store r on c.SHOP_ID = r.company_info_id where 1 = 1
and SHOP_ID = #{customerOrderQueryRequest.companyId}
and CUSTOMER_ID = #{customerOrderQueryRequest.customerId}
and (customer_name like CONCAT('%',#{customerOrderQueryRequest.queryText},'%') or customer_account like CONCAT('%',#{customerOrderQueryRequest.queryText},'%'))
and STAT_DATE = #{daylyDate}

