select sum(ORDER_NUM) as order_num, sum(ORDER_MONEY) as ORDER_MONEY, sum(ORDER_GOODS_NUM) as ORDER_GOODS_NUM, sum(PAY_NUM) as PAY_NUM, sum(PAY_GOODS_NUM) as PAY_GOODS_NUM, sum(PAY_MONEY) as PAY_MONEY, sum(REFUND_NUM) as REFUND_NUM, sum(REFUND_MONEY) as REFUND_MONEY, sum(REFUND_GOODS_NUM) as REFUND_GOODS_NUM, case when IFNULL(sum( ORDER_NUM ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( ORDER_MONEY ),0) / IFNULL(sum( ORDER_NUM ),0), DECIMAL ( 15, 2 ) ),0) end ORDER_PER_PRICE, CUSTOMER_ID, min(CUSTOMER_NAME) as CUSTOMER_NAME, min(CUSTOMER_ACCOUNT) as CUSTOMER_ACCOUNT from customer_day where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo}
and SHOP_ID = #{exportQuery.companyId}
GROUP BY CUSTOMER_ID ORDER BY ORDER_MONEY DESC limit #{exportQuery.beginIndex} , #{exportQuery.size}

select count(1) from (select count(CUSTOMER_ID) from customer_day where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo} and 1 = 1
and SHOP_ID = #{exportQuery.companyId}
GROUP BY CUSTOMER_ID) as temp

SELECT sum(ORDER_NUM) AS order_num, sum(ORDER_MONEY) AS ORDER_MONEY, sum(ORDER_GOODS_NUM) AS ORDER_GOODS_NUM, sum(PAY_NUM) AS PAY_NUM, sum(PAY_GOODS_NUM) AS PAY_GOODS_NUM, sum(PAY_MONEY) AS PAY_MONEY, sum(REFUND_NUM) AS REFUND_NUM, sum(REFUND_MONEY) AS REFUND_MONEY, sum(REFUND_GOODS_NUM) AS REFUND_GOODS_NUM, sum(CUSTOMER_COUNT) AS CUSTOMER_COUNT, min(CUSTOMER_LEVEL_NAME) AS CUSTOMER_LEVEL_NAME, case when IFNULL(sum( ORDER_NUM ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( ORDER_MONEY ),0) / IFNULL(sum( ORDER_NUM ),0), DECIMAL ( 15, 2 ) ),0) end ORDER_PER_PRICE, case when IFNULL(sum( CUSTOMER_COUNT ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( ORDER_MONEY ),0) / IFNULL(sum( CUSTOMER_COUNT ),0), DECIMAL ( 15, 2 ) ),0) end USER_PER_PRICE_THIRTY FROM customer_level_day where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo}
and SHOP_ID = #{exportQuery.companyId}
group BY CUSTOMER_LEVEL_ID ORDER BY ORDER_MONEY DESC limit #{exportQuery.beginIndex} , #{exportQuery.size}

select count(1) FROM (select count(CUSTOMER_LEVEL_ID) from customer_level_day
where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo}

    and SHOP_ID = #{exportQuery.companyId}

GROUP BY CUSTOMER_LEVEL_ID) temp

select sum(ORDER_NUM) as order_num, sum(ORDER_MONEY) as ORDER_MONEY, sum(ORDER_GOODS_NUM) as ORDER_GOODS_NUM, sum(PAY_NUM) as PAY_NUM, sum(PAY_GOODS_NUM) as PAY_GOODS_NUM, sum(PAY_MONEY) as PAY_MONEY, sum(REFUND_NUM) as REFUND_NUM, sum(REFUND_MONEY) as REFUND_MONEY, sum(REFUND_GOODS_NUM) as REFUND_GOODS_NUM, sum(CUSTOMER_COUNT) as CUSTOMER_COUNT, CITY_ID, case when IFNULL(sum( ORDER_NUM ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( ORDER_MONEY ),0) / IFNULL(sum( ORDER_NUM ),0), DECIMAL ( 15, 2 ) ),0) end ORDER_PER_PRICE, case when IFNULL(sum( CUSTOMER_COUNT ),0)=0 then 0 else IFNULL(CONVERT ( IFNULL(sum( ORDER_MONEY ),0) / IFNULL(sum( CUSTOMER_COUNT ),0), DECIMAL ( 15, 2 ) ),0) end USER_PER_PRICE_THIRTY from customer_region_day where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo}
and SHOP_ID = #{exportQuery.companyId}
GROUP BY CITY_ID ORDER BY ORDER_MONEY DESC limit #{exportQuery.beginIndex} , #{exportQuery.size}

select count(1) FROM (SELECT count(city_id) from customer_region_day
where STAT_DATE >= #{exportQuery.dateFrom} and STAT_DATE <= #{exportQuery.dateTo}

    and SHOP_ID = #{exportQuery.companyId}

GROUP BY CITY_ID) as temp

